    public UserTable delete(Integer usertableId) {
        LOGGER.debug("Deleting UserTable with id: {}", usertableId);
        UserTable deleted = this.wmGenericDao.findById(usertableId);
        if (deleted == null) {
            LOGGER.debug("No UserTable found with id: {}", usertableId);
            throw new EntityNotFoundException(MessageResource.create("com.wavemaker.runtime.entity.not.found"), UserTable.class.getSimpleName(), usertableId);
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

